/* Ejemplo para entender la sentencia System.out.print() y la sentencia
   System.out.println*/


public class PracticarVisualizaciones {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.print("Un árbol es una planta perenne, de tallo leñoso");
		System.out.print("\n\n");
		System.out.println("Los árboles presentan una mayor longevidad que otros tipos de plantas");
		System.out.println("Estoy usando el tabulador \t\t Esto está tabulado 2 veces");
		System.out.print("Los árboles son un importante");
		System.out.print('\t'); 
		System.out.println( "componente del paisaje natural debido a que previenen la erosión");
		System.out.println("Los árboles son un importante \tcomponente del paisaje natural debido a que previenen la erosión");


	}

}
